import React from "react";
import { Link } from "react-router-dom";

const JerseyCard = ({ jersey, onDelete }) => (
  <div style={{ border: "1px solid #ddd", padding: "16px", borderRadius: "8px" }}>
    <img src={`http://localhost:5000${jersey.image}`} alt={jersey.name} style={{ width: "100%", height: "200px", objectFit: "cover" }} />
    <h3>{jersey.name}</h3>
    <p>{jersey.description}</p>
    <p>Couleur: {jersey.color}</p>
    <Link to={`/edit/${jersey.id}`}>Modifier</Link>
    <button onClick={() => onDelete(jersey.id)}>Supprimer</button>
  </div>
);

export default JerseyCard;
